import React from "react";
import { makeStyles } from "@material-ui/core/styles";

import Grid from "@material-ui/core/Grid";

import Drink from "../components/Drink";
import { isInFavorite, ingredientsGenerator } from "../utils/functions";
const useStyles = makeStyles((theme) => ({
  root: {
    width: " 90vw",
    margin: "auto",
    marginTop: "10vh",
    backgroundColor: " rgba(255, 255, 255, 0.95)",
    minHeight: " 100vh",
    paddingTop: "50px",
    borderRadius: "5px",
  },
  subRoot: {
    width: " 100%",
    margin: "0",
    marginTop: "0",
    backgroundColor: "transparent",
    minHeight: " 100%",
    paddingTop: "0",
    borderRadius: "0",
  },
}));

function SearchResult(props) {
  console.log(props.drinksList);
  const classes = useStyles();
  return (
    <div className={classes.root}>
      <Grid
        className={
          classes.subRoot
        } /* container spacing={2}
            xs={12}
            sm={6}
            md={4}
            lg={1} */
      >
        <Grid
        /* container  */
        /*     item

          spacing={1} */
        /*          xs={12}
          sm={12} */
        /*          container
          direction="row"
          justify="center"
          alignItems="flex-start" */
        >
          {props.drinksList &&
            props.drinksList.map((drink, index) => (
              <Drink
                key={index}
                drink={drink}
                isInFavorite={isInFavorite(props.favoriteList, drink.idDrink)}
                saveToFavorite={props.saveToFavorite}
                byIngredients={props.byIngredients}
                ingredientsArr={ingredientsGenerator(drink)}
              />
            ))}
        </Grid>
      </Grid>
    </div>
  );
}

export default SearchResult;
